mapper.select:
SELECT * FROM( SELECT ROWNUM AS RN , A.SELL_PAGE_TITLE /* AS 판매상품명*/ , E.PRODUCT_PRICE * (1 - D.DISCOUNT) AS SELLPRICE /*AS 판매가격*/ , B.SELL_THUMB_IMAGE_FILE_NAME /*AS 썸네일이미지*/ , A.SELL_PAGE_NO /*AS 판매페이지주소*/ , A.FILE_PATH FROM T_SELL_PAGE A LEFT JOIN (SELECT SELL_PAGE_NO, MIN(SELL_THUMB_IMAGE_FILE_NAME) AS SELL_THUMB_IMAGE_FILE_NAME FROM T_THUMB_IMAGE GROUP BY SELL_PAGE_NO) B ON B.SELL_PAGE_NO = A.SELL_PAGE_NO LEFT JOIN T_SELL_ITEM_PAGE C ON C.SELL_PAGE_NO = A.SELL_PAGE_NO LEFT JOIN T_SELL_PRODUCT D ON D.SELL_PRODUCT_ID = C.SELL_PRODUCT_ID LEFT JOIN T_PRODUCT E ON E.PRODUCT_CODE = D.PRODUCT_CODE LEFT JOIN T_CATEGORY F ON F.CATEGORY_CODE = E.CATEGORY_CODE WHERE A.SELL_STATUS = 'Y'
AND A.SELL_PAGE_TITLE LIKE '%' || #{sellPage.title} || '%'
AND E.CATEGORY_CODE = #{product.categoryCode}
AND F.REF_CATEGORY_CODE = #{category.refCategoryCode}
AND E.PRODUCT_STATUS = #{product.productStatus} )
WHERE 1=1 AND RN > ( (TO_NUMBER(#{ pageNo }) - 1) * 10) AND RN <= ( TO_NUMBER(#{ pageNo }) * 10)

SELECT COUNT(*) FROM T_SELL_PAGE A LEFT JOIN (SELECT SELL_PAGE_NO, MIN(SELL_THUMB_IMAGE_FILE_NAME) AS SELL_THUMB_IMAGE_FILE_NAME FROM T_THUMB_IMAGE GROUP BY SELL_PAGE_NO) B ON B.SELL_PAGE_NO = A.SELL_PAGE_NO LEFT JOIN T_SELL_ITEM_PAGE C ON C.SELL_PAGE_NO = A.SELL_PAGE_NO LEFT JOIN T_SELL_PRODUCT D ON D.SELL_PRODUCT_ID = C.SELL_PRODUCT_ID LEFT JOIN T_PRODUCT E ON E.PRODUCT_CODE = D.PRODUCT_CODE LEFT JOIN T_CATEGORY F ON F.CATEGORY_CODE = E.CATEGORY_CODE WHERE A.SELL_STATUS = 'Y'
AND A.SELL_PAGE_TITLE LIKE '%' || #{sellPage.title} || '%'
AND E.CATEGORY_CODE = #{product.categoryCode}
AND F.REF_CATEGORY_CODE = #{category.refCategoryCode}
AND E.PRODUCT_STATUS = #{product.productStatus}

SELECT A.SELL_PAGE_TITLE /* AS 판매상품명*/ , E.PRODUCT_PRICE * (1 - D.DISCOUNT) AS SELLPRICE /*AS 판매가격*/ , B.SELL_THUMB_IMAGE_FILE_NAME /*AS 썸네일이미지*/ , A.SELL_PAGE_NO /*AS 판매페이지주소*/ , A.FILE_PATH FROM T_SELL_PAGE A LEFT JOIN T_THUMB_IMAGE B ON B.SELL_PAGE_NO = A.SELL_PAGE_NO LEFT JOIN T_SELL_ITEM_PAGE C ON C.SELL_PAGE_NO = A.SELL_PAGE_NO LEFT JOIN T_SELL_PRODUCT D ON D.SELL_PRODUCT_ID = C.SELL_PRODUCT_ID LEFT JOIN T_PRODUCT E ON E.PRODUCT_CODE = D.PRODUCT_CODE LEFT JOIN T_CATEGORY F ON F.CATEGORY_CODE = E.CATEGORY_CODE WHERE A.SELL_STATUS = 'Y'
AND A.SELL_PAGE_TITLE LIKE '%' || #{sellPage.title} || '%'
AND E.CATEGORY_CODE = #{product.categoryCode}
AND E.PRODUCT_STATUS = #{product.productStatus}
AND E.PRODUCT_PRICE * (1 - D.DISCOUNT) >= #{minPrice}
AND E.PRODUCT_PRICE * (1 - D.DISCOUNT) <= #{maxPrice}
ORDER BY SELLPRICE DESC
ORDER BY SELLPRICE ASC
ORDER BY A.SELL_PAGE_TITLE DESC
ORDER BY A.SELL_PAGE_TITLE ASC

SELECT CATEGORY_NAME
FROM T_CATEGORY
WHERE CATEGORY_CODE = #{categoryCode}

SELECT CATEGORY_NAME
FROM T_CATEGORY
WHERE CATEGORY_CODE = #{refCategoryCode}
