mapper.select:
SELECT COUNT(*) FROM ( SELECT ROWNUM AS RN , SELL_PRODUCT_ID , PRODUCT_NAME , PRODUCT_STATUS , PRODUCT_PRICE , DISCOUNT , PRODUCT_OPTION , SELL_PAGE_NO , SELL_PAGE_TITLE , SELL_STATUS FROM T_SELL_PRODUCT LEFT JOIN T_PRODUCT USING (PRODUCT_CODE) LEFT JOIN T_SELL_ITEM_PAGE USING (SELL_PRODUCT_ID) LEFT JOIN T_SELL_PAGE USING (SELL_PAGE_NO) WHERE 1=1
AND SELL_PRODUCT_ID = #{ code }
AND PRODUCT_NAME LIKE '%' || #{ name } || '%'
AND PRODUCT_STATUS = #{ productStatus }
AND CATEGORY_CODE = #{ categoryCode }
AND SELL_STATUS = #{ sellStatus }
)

SELECT * FROM ( SELECT ROWNUM AS RN , SELL_PRODUCT_ID , PRODUCT_NAME , PRODUCT_STATUS , PRODUCT_PRICE , DISCOUNT , PRODUCT_OPTION , SELL_PAGE_NO , SELL_PAGE_TITLE , SELL_STATUS FROM T_SELL_PRODUCT LEFT JOIN T_PRODUCT USING (PRODUCT_CODE) LEFT JOIN T_SELL_ITEM_PAGE USING (SELL_PRODUCT_ID) LEFT JOIN T_SELL_PAGE USING (SELL_PAGE_NO) WHERE 1=1
AND SELL_PRODUCT_ID = #{ code }
AND PRODUCT_NAME LIKE '%' || #{ name } || '%'
AND PRODUCT_STATUS = #{ productStatus }
AND CATEGORY_CODE = #{ categoryCode }
AND SELL_STATUS = #{ sellStatus }
)
WHERE 1=1 AND RN > ( (TO_NUMBER(#{ pageNo }) - 1) * 10) AND RN <= ( TO_NUMBER(#{ pageNo }) * 10)

SELECT COUNT(*) FROM ( SELECT ROWNUM AS RN , TB.BOARD_NO , TR.SELL_PAGE_NO , TP.PRODUCT_NAME , TB.BOARD_TITLE , TB.BOARD_CONTENT , TO_DATE(TB.BOARD_DATE, 'RRRR-MM-DD') as BOARD_DATE , TO_CHAR(TB.BOARD_DATE, 'HH24:MI:ss') as BOARD_TIME FROM T_BOARD TB JOIN T_REVIEW TR on TB.BOARD_NO = TR.BOARD_NO JOIN T_ORDER T on (TR.ORDER_NO = T.ORDER_NO AND TB.EMP_NO = T.EMP_NO) JOIN T_PRODUCT_ORDER TPO on T.ORDER_NO = TPO.ORDER_NO JOIN T_SELL_PRODUCT TSP on TSP.SELL_PRODUCT_ID = TPO.SELL_PRODUCT_ID JOIN T_PRODUCT TP on TP.PRODUCT_CODE = TSP.PRODUCT_CODE WHERE 1=1
AND TSP.SELL_PRODUCT_ID = #{ search.id }
AND TP.PRODUCT_NAME LIKE '%' || #{ search.name } || '%'
AND BOARD_DATE >= #{ search.startDate } AND BOARD_DATE <= #{ search.endDate }
AND BOARD_DATE >= #{ search.startDate }
AND BOARD_DATE <= #{ search.endDate }
) WHERE 1=1

SELECT * FROM ( SELECT ROWNUM AS RN , TB.BOARD_NO , TR.SELL_PAGE_NO , TP.PRODUCT_NAME , TB.BOARD_TITLE , TB.BOARD_CONTENT , TO_DATE(TB.BOARD_DATE, 'RRRR-MM-DD') as BOARD_DATE , TO_CHAR(TB.BOARD_DATE, 'HH24:MI:ss') as BOARD_TIME FROM T_BOARD TB JOIN T_REVIEW TR on TB.BOARD_NO = TR.BOARD_NO JOIN T_ORDER T on (TR.ORDER_NO = T.ORDER_NO AND TB.EMP_NO = T.EMP_NO) JOIN T_PRODUCT_ORDER TPO on T.ORDER_NO = TPO.ORDER_NO JOIN T_SELL_PRODUCT TSP on TSP.SELL_PRODUCT_ID = TPO.SELL_PRODUCT_ID JOIN T_PRODUCT TP on TP.PRODUCT_CODE = TSP.PRODUCT_CODE WHERE 1=1
AND TSP.SELL_PRODUCT_ID = #{ search.id }
AND TP.PRODUCT_NAME LIKE '%' || #{ search.name } || '%'
AND BOARD_DATE >= #{ search.startDate } AND BOARD_DATE <= #{ search.endDate }
AND BOARD_DATE >= #{ search.startDate }
AND BOARD_DATE <= #{ search.endDate }
) WHERE 1=1
AND RN > ( (TO_NUMBER(#{ pageNo }) - 1) * 10) AND RN <= ( TO_NUMBER(#{ pageNo }) * 10)

SELECT
COUNT(*)
FROM (
SELECT ROWNUM AS RN
, BOARD_NO
, SELL_PAGE_NO
, BOARD_TITLE
, BOARD_CONTENT
, TO_DATE(BOARD_DATE, 'RRRR-MM-DD') as BOARD_DATE
, TO_CHAR(BOARD_DATE, 'HH24:MI:ss') as BOARD_TIME
FROM T_BOARD
JOIN T_QNA USING (BOARD_NO)
JOIN T_SELL_PAGE USING (SELL_PAGE_NO)
WHERE 1=1

    AND SELL_PRODUCT_ID = #{ search.id }


    AND PRODUCT_NAME = #{ search.name }



    AND BOARD_DATE >= #{ search.startDate }
    AND BOARD_DATE <= #{ search.endDate }


    AND BOARD_DATE >= #{ search.startDate }


    AND BOARD_DATE <= #{ search.endDate }


)
WHERE BOARD_DATE IS NOT NULL

SELECT * FROM ( SELECT ROWNUM AS RN , BOARD_NO , SELL_PAGE_NO , BOARD_TITLE , BOARD_CONTENT , TO_DATE(BOARD_DATE, 'RRRR-MM-DD') as BOARD_DATE , TO_CHAR(BOARD_DATE, 'HH24:MI:ss') as BOARD_TIME FROM T_BOARD JOIN T_QNA USING (BOARD_NO) JOIN T_SELL_PAGE USING (SELL_PAGE_NO) WHERE 1=1
AND SELL_PRODUCT_ID = #{ search.id }
AND PRODUCT_NAME = #{ search.name }
AND BOARD_DATE >= #{ search.startDate } AND BOARD_DATE <= #{ search.endDate }
AND BOARD_DATE >= #{ search.startDate }
AND BOARD_DATE <= #{ search.endDate }
) WHERE BOARD_DATE IS NOT NULL
AND RN > ( (TO_NUMBER(#{ pageNo }) - 1) * 10) AND RN <= ( TO_NUMBER(#{ pageNo }) * 10)

SELECT
       PRODUCT_CODE
     , PRODUCT_NAME
  FROM T_PRODUCT
 WHERE 1=1

   AND PRODUCT_STATUS = #{ productStatus }


    AND PRODUCT_OPTION = #{ productOption }


    AND CATEGORY_CODE = #{ categoryCode }

mapper.insert:
INSERT INTO T_SELL_PRODUCT
VALUES
(
  'SP' || LPAD(SEQ_SELL_PRODUCT_ID.nextval, 6, '0')
, #{ code }
, #{ discount }
, DEFAULT
)

    SELECT 'SP' || LPAD(SEQ_SELL_PRODUCT_ID.currval, 6, '0')
      FROM DUAL

INSERT INTO T_SELL_PAGE
VALUES
(
    'SN' || LPAD(SEQ_SELL_PAGE_NO.nextval, 10, '0')
,   #{ title }
,   #{ detailImageFileName , jdbcType=NULL}
,   #{ detailImageOriginFileName , jdbcType=NULL}
,   #{ path }
,   SYSDATE
,   #{ sellStatus }
)

    SELECT 'SN' || LPAD(SEQ_SELL_PAGE_NO.currval, 10, '0')
      FROM DUAL

INSERT INTO T_SELL_ITEM_PAGE
VALUES
(
    #{ no }
,   #{ id }
)

INSERT INTO T_THUMB_IMAGE
VALUES
(
    #{ no }
,   #{ thumbnailImageFileName , jdbcType=NULL}
,   #{ thumbnailImageOriginFileName , jdbcType=NULL}
)

mapper.delete:
DELETE
FROM T_SELL_PRODUCT
WHERE SELL_PRODUCT_ID = #{ id }

mapper.update:
UPDATE T_SELL_PAGE
   SET SELL_PAGE_TITLE = #{ title }
     , SELL_STATUS = #{ sellStatus }

     , SELL_DETAIL_IMAGE_FILE_NAME = #{ detailImageFileName, jdbcType=NULL}
     , SELL_DETAIL_IMAGE_ORGIN_FILE_NAME = #{ detailImageOriginFileName, jdbcType=NULL}

 WHERE SELL_PAGE_NO = #{ no }

UPDATE T_SELL_PRODUCT
   SET DISCOUNT = #{ discount }
 WHERE SELL_PRODUCT_ID = #{ id }

UPDATE T_BOARD
   SET IS_PRIVATE
 WHERE BOARD_NO = #{ no }

mapper.delete:
DELETE
  FROM T_THUMB_IMAGE
 WHERE SELL_PAGE_NO = #{ no }

DELETE
  FROM T_SELL_PAGE
 WHERE SELL_PAGE_NO = #{ no }
